class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        for(int i = 0; i < n; i++) {
            nums[i] -= target[i];
        }
        long ans = 0;
        for(int i = 0; i < n; ) {
            if(nums[i] == 0){
                i++;
                continue;
            }
            int j = i;
            int x = -1;
            if(nums[i] > 0) {
                x = 1;
            } 
            while(j < n && x * nums[j] > 0)j++;
            int pre = 0;
            for(int k = i; k < j; k++) {
                int cur = Math.abs(nums[k]);
                // System.out.println(cur+" "+pre);
                if(cur > pre) {
                    ans += cur - pre;
                    pre = cur;
                } else {
                    pre = cur;
                }
            }
            i = j;
        }
        return ans;
    }
}
