class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        for(int i=0;i<nums.length;i++){
            nums[i]-=target[i];
        }
        for(int i=0;i<nums.length;i++){
            if(nums[i]==0) continue;
            if(i==nums.length-1||nums[i+1]==0){
                ans+=(long)Math.abs(nums[i]);
                continue;
            }
            if(nums[i]>0){
                int j=i+1;
                int tmp = nums[i];
                while(j<nums.length&&nums[j]>0){
                    tmp = Math.max(tmp, nums[j]);
                    if(nums[j] < tmp){
                        ans+=(long) (tmp-nums[j]);
                        tmp = nums[j];
                    }
                    j++;
                }
                ans+=(long)tmp;
                i=j-1;

            }else if(nums[i]<0){
                int j=i+1;
                int tmp = nums[i];
                while(j<nums.length&&nums[j]<0){
                    tmp = Math.min(tmp, nums[j]);
                    if(nums[j] > tmp){
                        ans+=(long) (nums[j]-tmp);
                        tmp = nums[j];
                    }
                    j++;

                }
                ans+=(long)(-tmp);
                i=j-1;
            }
        }
        return ans;
    }
}
