class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        for i in range(len(nums)):
            diff.append(target[i] - nums[i])
        ops = 0
        carryover = 0
        for i in range(len(diff)):
            if diff[i] < 0:
                if carryover < 0:
                    if carryover > diff[i]:
                        ops -= diff[i] - carryover
                else:
                    ops -= diff[i]
            elif diff[i] > 0:
                if carryover > 0:
                    if carryover < diff[i]:
                        ops += diff[i] - carryover
                else:
                    ops += diff[i]
            carryover = diff[i]
        return ops
