class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]

        pref = [0]
        prev = 0

        for i in diff:
            pref.append(i - prev)
            prev = i
        print(pref)

        negs = []
        pos = []
        for i in pref:
            if i < 0:
                negs.append(abs(i))
            else:
                pos.append(i)
        ops = 0
        while len(negs) and len(pos):
            x = negs.pop()
            y = pos.pop()
            if x > y:
                ops += y
                negs.append(x - y)
            elif y > x:
                ops += x
                pos.append(y - x)
            else:
                ops += x
        negs.append(0)
        pos.append(0)
        return ops + sum(negs) + sum(pos)
