typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> diff(nums.size(), 0);
        for(int i = 0; i < n; i++){
            diff[i] = target[i] - nums[i];
        }
        ll ans = 0;
        ll prev = 0;
        for(int i = 0; i < n; i++){
            ll inc = diff[i] - prev;
            if(prev == 0){
                ans += abs(diff[i]);
            }
            else if(prev > 0){
                if(diff[i] >= 0){
                    ans += max(0ll, inc);
                }
                else {
                    ans += abs(inc) - prev;
                }
            }
            else {
                inc *= -1;
                if(diff[i] <= 0){
                    ans += max(0ll, inc);
                }
                else {
                    ans += abs(inc) + prev;
                }
            }
            prev = diff[i];
        }
        return ans;
    }
};
