class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int size = nums.size();
        vector<int> v(size, 0);
        for (int i = 0; i < size; ++i) {
            v[i] = target[i] - nums[i];
        }
        ll ans = abs(v[0]);
        for (int i = 1; i < size; ++i) {
            if ((ll)v[i] * v[i - 1] >= 0) {
                ans += max(0, abs(v[i]) - abs(v[i - 1]));
            } else {
                ans += abs(v[i]);
            }
        }
        return ans;
    }
};
