class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        vector<long long>v(n);
        for(int i=0;i<n;i++)
        {
            v[i]=target[i]-nums[i];
        }
        long long t=0;

        for(int i=0;i<n;i++)
        {
            long long p=(t*v[i]);
            if(v[i]==0)
            {
                t=0;
            }
            else if(t==0)
            {
                ans+=abs(v[i]);
                t=v[i];
            }
            else if(p>0)
            {
                if(abs(v[i])>abs(t))
                {
                    ans+=abs(v[i]-t);
                }
                t=v[i];
            }
            else
            {
                ans+=abs(v[i]);
                t=v[i];
            }
        }
        return ans;

    }
};
