class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int> diff(nums.size());
        int n=nums.size();
        vector<int> sign(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
            if(diff[i]>0) sign[i]=1;
            else sign[i]=-1;
            diff[i]=abs(diff[i]);
        }
        int maxi=0;
        for(int i=0;i<n;i++){
            if(i>0 && sign[i]!=sign[i-1])
                maxi=0;
            if(maxi<diff[i]){
                ans+=diff[i]-maxi;
                maxi=diff[i];
            }
            maxi=min(maxi,diff[i]);
        }
        return ans;
    }
};
