typedef long long ll;
const ll N=1e5+10;
class Solution {
public:
    ll A[N],n,cha[N];
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        ll ans=0,k1=0,k2=0;
        n=nums.size();
        for(ll a=1;a<=n;a++) A[a]=target[a-1]-nums[a-1];
        for(ll a=1;a<=n;a++) cha[a]=A[a]-A[a-1];
        for(ll a=1;a<=n;a++)
        {
            if(cha[a]<0) k2+=-cha[a];
            else k1+=cha[a];
        }




        return max(k1,k2);

    }
};
