class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        nums.push_back(0);
        target.push_back(0);
        long long ans=0;
        int n=nums.size();
        vector<int> a(n);
        vector<int> at(n);
        a[0]=nums[0];
        at[0]=target[0];
        for(int i=1;i<n;i++){
            a[i]=nums[i]-nums[i-1];
            at[i]=target[i]-target[i-1];
        }
        for(int i=0;i<n;i++){
            if(a[i]>at[i])ans+=a[i]-at[i];
        }
        return ans;
    }
};
