class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [i - j for i, j in zip(nums, target)]
        ans = 0
        heap = [0]

        def helper(heap, new):
            cost = 0
            if heap[-1] == new:
                return cost

            if (heap[-1] > 0 and new < 0) or (heap[-1] < 0 and new > 0):
                cost += helper(heap, 0)
                heap.append(new)
                return cost

            while abs(heap[-1]) > abs(new):
                cost = max(cost, abs(heap[-1]) - abs(new))
                heap.pop()
            heap.append(new)
            return cost


        for d in diff:
            ans += helper(heap, d)
        ans += helper(heap, 0)
        return ans
