class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long pnum = 0, nnum = 0;
        if (nums[0] > target[0])
            pnum += nums[0] - target[0];
        else
            nnum -= nums[0] - target[0];
        for (int i = 0; i < n - 1; i++) {
            long long val = (nums[i + 1] - nums[i]) - (target[i + 1] - target[i]);
            if (val>0)
                pnum += val;
            else
                nnum -= val;
        }
        return max(pnum,nnum);
    }
};
