typedef long long LL;
class Solution {
public:
    int sgn(int x) {
        if (x > 0) return 1;
        if (x < 0) return -1;
        return 0;
    }
    LL solve(vector<int>& a) {
        // for (int i : a) cout << i << ' ';
        // cout << endl;

        int n = a.size();
        a.push_back(0);

        LL ans = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] > a[i + 1]) ans += a[i] - a[i + 1];
        }
        // cout << ans << endl;
        return ans;
    }
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        int n = a.size();

        for (int i = 0; i < n; i++) {
            a[i] -= t[i];
        }

        LL ans = 0;
        vector<int> tmp;
        for (int i = 0; i < n; ) {
            int j = i;
            while (j < n && sgn(a[i]) == sgn(a[j])) {
                j++;
            }

            // cout << "l r " << i << ' ' << j << endl;

            if (sgn(a[i]) > 0) {
                tmp.clear();
                for (int k = i; k < j; k++) {
                    tmp.push_back(a[k]);
                }
                ans += solve(tmp);
            } else if (sgn(a[i]) < 0) {
                tmp.clear();
                for (int k = i; k < j; k++) {
                    tmp.push_back(-a[k]);
                }
                ans += solve(tmp);
            }

            i = j;
        }
        return ans;
    }
};
