class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dn = [0]
        dt = [0]
        pre = 0
        for num in nums:
            dn.append(num - pre)
            pre = num
        pre = 0
        for t in target:
            dt.append(t - pre)
            pre = t 

        cnt = 0
        ans = 0
        for i in range(len(dn)):
            d = dt[i] - dn[i]
            if d*cnt >= 0:
                ans += abs(d)
            elif abs(cnt) < abs(d):
                ans += abs(cnt + d)
            cnt += d 
        return ans
