class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        res = 0
        prev = 0
        for i in range(n):
            diff = target[i] - nums[i]
            if diff >= 0:
                if diff > prev:
                    res += diff - max(0, prev)

                prev = diff
            else:
                if diff < prev:
                    res += min(0, prev) - diff

                prev = diff


        return res
