public class Solution {
    public long MinimumOperations(int[] nums, int[] target) {
       int[] diff = new int[nums.Length];
       for (int i = 0; i < nums.Length; i ++){
        diff[i] = target[i] - nums[i];
       } 

       long ans = 0;
       int last = 0;
       int maxAdd = 0;
       int maxSub = 0;
        for (int i = 0; i < nums.Length; i ++){
            if (i == 0){
                ans += Math.Abs(diff[i]);
                if (diff[i] > 0){
                    maxSub = diff[i];
                }else{
                    maxAdd = -diff[i];
                }
            }else if (diff[i] == 0){
                maxAdd = 0;
                maxSub = 0;
            }else if (diff[i] > 0){
                if (maxSub >= diff[i]){
                    maxSub = diff[i];
                }else{
                    ans += (diff[i] - maxSub);
                    maxSub = diff[i];
                }
                maxAdd = 0;
            }else{
                if (maxAdd >= -diff[i]){
                    maxAdd = -diff[i];
                }else{
                    ans += -diff[i] - maxAdd;
                    maxAdd = -diff[i];
                }
                maxSub = 0;
            }
        }

       return ans;
    }
}
