class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = 0; i < n; i++) {
            target[i] -= nums[i];
        }
        long long pre = target[0];
        long long ans = 0;
        for(int i = 1; i < n; i++) {
          //  cout << pre << ' '<< ans << endl;
            if(target[i] >= 0 && pre >= 0) {
                if(pre <= target[i]) {
                    pre = target[i];
                } else {
                    ans += abs(target[i] - pre);
                    pre = target[i];
                }
            } else if(target[i] < 0 && pre < 0) {
                if(pre >= target[i]) {
                    pre = target[i];
                } else {
                    ans += abs(pre - target[i]);
                    pre=target[i];
                }
            } else {
                ans += abs(pre);
                pre = target[i];
            }
        }

        ans += abs(pre);
        return ans;
    }
};
