class Solution {
public:
    typedef long long LL;
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        LL res = 0, n = a.size(), sum = 0;
        for(int i = 0; i < n; )
        {
            if(a[i] == b[i])continue;
            bool f = (a[i] - b[i] < 0);
            sum = abs(a[i] - b[i]);
            res += sum;
            int j;
            for(j = i + 1; j < n; j ++)
            {
                bool f1 = (a[j] - b[j] < 0);
                if(f == f1){
                    if(abs(a[j] - b[j]) > sum)res += abs(a[j] - b[j]) - sum;
                    sum = abs(a[j] - b[j]);
                }
                else break;
            }
            i = j;
        }
        return res;
    }
};
