class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> A;
        for (int i = 0; i < nums.size(); ++i) {
            A.push_back(target[i]-nums[i]);
        }
        long long res = 0;
        int c = 0;
        for (int i = 0; i < A.size(); ++i) {
            if (A[i]>=0 && c>=0) {
                if (A[i]>c) {
                    res += A[i]-c;
                }
            } else if (A[i]<=0 && c<=0){
                if (A[i]<c) {
                    res += c-A[i];
                }
            } else {
                res += abs(A[i]);
            }
            c = A[i];
        }
        return res;
    }
};
