def count_diffs(diffs: set):
    if len(diffs) == 0:
        return 0
    nums = []

class Solution:


    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        prev_diff = 0
        result = 0 
        for a, b in zip(nums, target):
            diff = a - b
            if prev_diff == diff:
                continue 
            if diff == 0:
                prev_diff = diff
                continue 
            if prev_diff == 0:
                prev_diff = diff
                result += abs(diff)
                continue 
            if (diff > 0) != (prev_diff > 0):
                prev_diff = diff
                result += abs(diff)
                continue 

            if (diff > 0) == (prev_diff > 0):
                prev = abs(prev_diff)
                curr = abs(diff)
                prev_diff = diff 

                if prev >= curr:
                    continue
                else:
                    result += (curr - prev)
        return result
