class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++)nums[i]-=target[i];
        vector<int>a(n);
        a[0]=nums[0];
        for(int i=1;i<n;i++)a[i]=nums[i]-nums[i-1];
        long long sum1,sum2;
        sum1=accumulate(a.begin(),a.end(),0ll);
        sum2=accumulate(a.begin(),a.end(),0ll,[](long long sum,int a){return sum+abs(a);});
        assert((sum2-sum1)%2==0);
        return abs(sum1)+(sum2-abs(sum1))/2;
    }
};
