#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll> diff;
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            diff.push_back(target[i]-nums[i]);
        }
        ll ans=abs(diff[0]);
        for(int i=1;i<n;i++)
        {
            if(diff[i]>=0)
            {
                if(diff[i-1]<0) ans+=diff[i];
                else ans+=max(diff[i]-diff[i-1],0LL);
            }
            else
            {
                if(diff[i-1]>=0) ans+=abs(diff[i]);
                else ans+=max(diff[i-1]-diff[i],0LL);
            }
        }
        return ans;
    }
};
