class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int n = nums.size();
        int d1 = 0, d2 = 0, diff = 0;
        long long opt = 0, net = 0;
        diff = target[0] - nums[0];
        if(diff > 0) opt += diff;
        else net += diff;
        for(int i = 1; i < n; i++) {
            d1 = nums[i] - nums[i - 1];
            d2 = target[i] - target[i - 1];
            diff = d2 - d1;
            if(diff > 0) opt += diff;
            else net += diff;
        }
        res = min(abs(opt), abs(net)) + abs(opt + net);
        return res;


    }
};
