class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> a(n), b(n);
        a[0] = nums.front(), b[0] = target.front();
        for(int i = 1; i < n; ++ i)
            a[i] = nums[i] - nums[i - 1]; 
        for(int i = 1; i < n; ++ i)
            b[i] = target[i] - target[i - 1];
        vector<int> diff(n);
        for(int i = 0; i < n; ++ i)
            diff[i] = b[i] - a[i];
        long long pos = 0, neg = 0;
        for(int i = 0; i < n; ++ i)
            if (diff[i] > 0)
                pos += diff[i];
            else if (diff[i] < 0)
                neg += diff[i];
        neg = -neg;
        long long minn = min(pos, neg);
        pos -= minn;
        neg -= minn;
        return minn + pos + neg;    
    }
};
