class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> d(n);


        for (int i = 0; i < n; ++i)
        {
            d[i] = nums[i] - target[i];
        }

    long long pr = 0;
    long long ans  = 0;


    for (int i = 0; i < n; ++i) 
    {
        if(i==0)
        {
            ans+=abs(d[i]);
        }
        else
        {
            if(d[i]>0&&d[i-1]<0)
            {
                ans+=abs(d[i]);
            }
            else if(d[i]<0&&d[i-1]>0)
            {
                ans+=abs(d[i]);
            }
            else
            {
                if(abs(d[i])>abs(d[i-1]))
                {
                    ans+=abs(d[i])-abs(d[i-1]);
                }
            }
        }
    }

    return ans;
    }
};
