class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        if(n==0)return 0;
        long long res=0;
        for(int i=0;i<n;i++){
            target[i]-=nums[i];
        }
        res+=abs(target[0]);
        for(int i=1;i<n;i++){
            if(target[i-1]>0 && target[i]>target[i-1])res+=target[i]-target[i-1];
            else if(target[i-1]<0 && target[i]<target[i-1])res+=target[i-1]-target[i];
            else if(target[i-1]>=0 && target[i]<0 || target[i-1]<=0 && target[i]>0)res+=abs(target[i]);
        }
        return res;
    }
};
