class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        s = [0]
        p = 0
        for v in map(lambda x: x[0] - x[1], zip(nums, target)):
            s.append(v - p)
            p = v
        p, n = 0, 0
        for v in s:
            if v > 0:
                p += v
            else:
                n -= v
        return max(p, n)
