class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int inc = 0;
        int dec = 0;
        for(int i = 0 ; i < nums.length ; i++){
            int val = nums[i]+inc-dec;
            int diff = target[i]-val;
            if(diff>0){
                int min = Math.min(diff,dec);
                diff -= min;
                dec -= min;
            }
            if(diff<0){
                int min = Math.min(-diff,inc);
                diff += min;
                inc -= min;
            }
            if(diff>0){
                inc+=diff;
                ans += diff;
            }else{
                dec+=-diff;
                ans += -diff;
            }
        }
        return ans;
    }
}
