class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        arr = []
        for i in range(n):
            arr.append(nums[i] - target[i])

        res = 0
        pre = 0
        for num in arr:
            if num < 0:
                pre = 0
                continue
            if num > pre:
                res += num - pre

            pre = num

        pre = 0
        for num in arr:
            if num > 0:
                pre = 0
                continue
            if num < pre:
                res += pre - num

            pre = num

        return res
