class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long sum0 = 0, sum1 = 0, last = 0; 
        for (int i = 0; i < n; ++i) {
            long long now = nums[i] - target[i];
            if (now - last >= 0) sum0 += now - last;
            else sum1 += last - now;
            last = now;
        }
        return max(sum1, sum0);
    }
};
