class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int size = nums.size();
        long long answ = 0;
        answ+=abs(nums[0]-target[0]);
        for(int i=1;i<size;i++)
        {
            if(nums[i]==target[i])
            {
                continue;
            }
            else
            {
                int temp = nums[i-1]-target[i-1];
                int require = nums[i]-target[i];
                if(temp<0 && require<0)
                {
                    if((abs(temp))>abs((require)))
                    {
                        continue;
                    }
                    else
                    {
                        answ=answ+ abs(require) - abs(temp);
                    }
                }
                else if(temp>0 && require>0)
                {
                    if(temp>require)
                    {
                        continue;
                    }
                    else
                    {
                        answ=answ+(require-temp);
                    }
                }
                else
                {
                    answ=answ+abs(require);
                }
            }
        }
        return answ;
    }
};
