class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = (int) nums.size();
        vector<long long> a(n);

        for (int i = 0; i < n; i++)
            a[i] = nums[i] - target[i];

        vector<long long> cur;
        cur.push_back(abs(a[0]));
        long long res = 0;

        function<long long()> getAns = [&] () {
            int sz = (int) cur.size();
            if (sz == 0) return 0ll;

            long long ans = cur[0];

            for (int i = 1; i < sz; i++)
                ans += max(cur[i] - cur[i - 1], 0ll);

            return ans;
        };

        for (int i = 1; i < n; i++) {
            if (a[i] == 0) {
                res += getAns();
                cur.clear();
                continue;
            }

            bool p1 = (a[i] >= 0);
            bool p2 = (a[i - 1] >= 0);

            if (p1 == p2) {
                cur.push_back(abs(a[i]));
            } else {
                res += getAns();
                cur.clear();
                cur.push_back(abs(a[i]));
            }
        }

        res += getAns();
        return res;
    }
};
