class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n=nums.size();
        vector<int> t(n,0);
        for (int i=0;i<n;i++)
        {
            t[i]=tar[i]-nums[i];
        }
        int pos=0;
        long long  m=0,ans=0;
        ans= abs(t[0]);
        m=ans;
        if(t[0]>=0)
        pos=1;
        for(int i=1;i<n;i++)
        {
            if(t[i]<0 && pos==1)
            {
                ans+= abs(t[i]);
                m= abs(t[i]);
                pos=0;
                continue;
            }
            if(t[i]>=0 && pos==0)
            {
                ans+= abs(t[i]);
                m= abs(t[i]);
                pos=1;
                continue;
            }
            if(m >= abs(t[i]))
            {

                m=abs(t[i]);

                continue;
            }
            else
            {

                ans+= abs(t[i])-m;
                m=abs(t[i]);

            }


        }
        return ans;
    }
};
