class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        std::vector<int> a(n + 1, 0), b(n + 1, 0);
        a[1] = nums[0], b[1] = target[0];
        for (int i = 2; i <= n; i++) {
            a[i] = nums[i - 1] - nums[i - 2];
            b[i] = target[i - 1] - target[i - 2];
        }
        long long f = 0, g = 0;
        for (int i = 1; i <= n; i++) {
            if (a[i] <= b[i]) {
                f += b[i] - a[i];
            } else {
                g += a[i] - b[i];
            }
        }
        return std::min(f, g) + std::max(f, g) - std::min(f, g);
    }
};
