class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

       long long inc=0,sum=0,op=0;

        for(int i=0;i<n;i++){
            if(nums[i]==target[i]){
                inc=0;
                continue;
            }

            if(nums[i]<target[i]){
               int req=target[i]-nums[i];
               if(inc<req){
                 if(inc<0)inc=0;
                 op=(req-inc);
                 sum+=op;
                 inc=req;
               }
               else {
                  inc=req;
               }
            }
            else{
               int req=target[i]-nums[i];
               if(inc<=req){
                   inc=req;
                   continue;
               }
               else{   
                   if(inc>0)inc=0;
                   op=inc-req;
                   sum+=op;
                   inc=req;
               }
            }
            //cout<<sum<<" ";
        }

        return sum;
    }
};
