class Solution {

    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n + 1];
        for (int i = 0; i < n; i++) {
            diff[i] = nums[i] - target[i];
        }
        long ans = 0;
        long g = 0;
        for (int i = n; i > 0; i--) {
            diff[i] -= diff[i - 1];
            if (diff[i] > 0) {
                ans += diff[i];
            } else {
                g -= diff[i];
            }
        }
        return Math.max(ans, g);
    }
}
