class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        long maxm = 0;
        int n = nums.length;
        int f = 2;
        for(int i = 0; i < n; i++) {

            if(nums[i] > target[i]) {

                if(i == 0) ans += nums[i] - target[i];
                else {
                    if(nums[i - 1] > target[i - 1]) ans += Math.max(0, (nums[i] - target[i]) - (nums[i - 1] - target[i - 1]));
                    else if(nums[i - 1] <= target[i - 1]) ans += nums[i] - target[i];
                }
            } else if(nums[i] < target[i]) {
                if(i == 0) ans += target[i] - nums[i];
                else {
                    if(nums[i - 1] < target[i - 1]) ans += Math.max(0, (target[i] - nums[i]) - (target[i - 1] - nums[i - 1]));
                    else if(nums[i - 1] >= target[i - 1]) ans += target[i] - nums[i];
                }
            }

            // System.out.println(ans);

        }
        return ans;

    }
}
