using namespace std;
using LL = long long;
const int INF = 0x3f3f3f3f;
const LL LINF = 0x3f3f3f3f3f3f3f3f;

class Solution
{
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target)
    {
        int n = nums.size();
        for (int i = 0; i < n; i++)
            nums[i] -= target[i];
        LL ans1 = 0, ans2 = 0;
        for (int i = n - 1; i >= 0; i--)
        {
            if (i > 0)
                nums[i] -= nums[i - 1];
            if (nums[i] > 0)
                ans1 += nums[i];
            else
                ans2 -= nums[i];
        }
        return max(ans1, ans2);
    }
};
