class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();

        long long res = 0, last = 0;
        for (int i = 0; i < n; ++i) {
            long long diff = target[i] - nums[i];
            if (last == 0 || diff == 0) {
                res += abs(diff);
            } else if (last > 0 == diff > 0) {
                if (abs(diff) > abs(last)) {
                    res += abs(diff) - abs(last);
                }
            } else {
                res += abs(diff);
            }
            last = diff;
        }
        return res;
    }
};
