typedef long long ll;

ll A[100000];

ll f(int l, int r) {
    for (int i = l; i <= r; ++i)
        A[i] = abs(A[i]);
    ll ans = 0;
    for (int i = l; i < r; ++i) {
        ans += max(A[i] - A[i + 1], 0ll);
    }
    ans += A[r];
    return ans;
}

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        for (int i = 0; i < n; ++i)
            A[i] = nums[i] - target[i];
        ll l = 0;
        ll ans = 0;
        for (int i = 1; i < n; ++i) {
            if (A[i] * A[i - 1] < 0) {
                ans += f(l, i - 1);
                l = i;
            }
        }
        ans += f(l, n - 1);
        return ans;
    }
};
