class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        pos = []
        neg = []
        for i in range(len(target)):
            if target[i]-nums[i]<0:
                neg.append(nums[i]-target[i])
                pos.append(0)
            else:
                pos.append(target[i]-nums[i])
                neg.append(0)
        base = 0
        ret = 0
        for i in pos:
            if i==0:
                base=0
            elif i<base:
                base = i
            elif i>base:
                ret+=i-base
                base = i
        base = 0
        for i in neg:
            if i==0:
                base=0
            elif i<base:
                base = i
            elif i>base:
                ret+=i-base
                base = i
        return ret

