func minimumOperations(nums []int, target []int) int64 {
    n := len(nums)
    diff := target[0]-nums[0]
    ans := abs(diff)
    for i := 1; i < n; i++ {
        need := target[i]-nums[i]
        if need == 0 {
            diff = 0
            continue
        }
        if need * diff < 0 {
            diff = 0
        }
        if need > 0 {
            if diff < need {
                ans += need-diff
            }
            diff = need
        } else {
            if -diff < -need {
                ans += diff-need
            }
            diff = need
        }
    }
    return int64(ans)
}

func abs(x int) int {
    if x < 0 {
        return -x
    }
    return x
}
