class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            nums[i] = target[i] - nums[i];
        }
        long long ans = abs(nums[0]);
        for (int i = 1; i < n; i++) {
            if ((nums[i] ^ nums[i - 1]) < 0)
                ans += abs(nums[i]);
            else if ((nums[i - 1] <= nums[i] && nums[i] > 0) || (nums[i - 1] >= nums[i] && nums[i] < 0))
                ans += abs(nums[i] - nums[i - 1]);
        }
        return ans;
    }
};
