class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr = [x - y for x, y in zip(nums, target)]
        arr.append(0)
        #print(arr)
        res = 0
        is_pos = arr[0] >= 0
        stk = [(arr[0], 0)]
        #print(0, stk, res, is_pos)
        for i, num in enumerate(arr[1:], start=1):
            if is_pos:
                if num < 0:
                    is_pos = False
                    if stk: res += stk[-1][0]
                    stk = []
                else:
                    if stk and stk[-1][0] > num:
                        num2, i2 = stk.pop()
                        res += num2 - num
                    while stk and stk[-1][0] > num:
                        stk.pop()
            else:
                if num > 0:
                    is_pos = True
                    if stk: res -= stk[-1][0]
                    stk = []
                else:
                    if stk and stk[-1][0] < num:
                        num2, i2 = stk.pop()
                        res += num - num2
                    while stk and stk[-1][0] < num:
                        stk.pop()
            if not stk or num != stk[-1][0]:
                stk.append((num, i))
            #print(i, stk, res, is_pos)
        return res
