class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> dp(n);
        dp[0] = abs(nums[0] - target[0]);
        for (int i = 1; i < n; i++) {
            ll x = target[i] - nums[i];
            ll y = target[i - 1] - nums[i - 1];
            if (x == 0) {
                dp[i] = dp[i - 1];
            } else if (y == 0) {
                dp[i] = dp[i - 1] + abs(x);
            } else if (x * y > 0) {
                if (abs(y) > abs(x)) {
                    dp[i] = dp[i - 1];
                } else {
                    dp[i] = dp[i - 1] + abs(x) - abs(y);
                }
            } else {
                dp[i] = dp[i - 1] + abs(x);
            }
        }
        return dp[n - 1];
    }
};
