class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        long long ans = 0;
        long long inc = 0;
        long long dec = 0;
        for(int i=0;i<n;i++)
        {
            long long cur = b[i] - a[i];

            if(cur>0)
            {
                if(inc>=cur)
                {
                    inc = cur;
                }
                else
                {
                    ans += (cur-inc);
                    inc+=(cur-inc);
                }
                dec = 0;
            }
            else
            {
                cur*=-1;
                if(dec>=cur)
                {
                    dec = cur;
                }
                else
                {
                    ans += (cur-dec);
                    dec+=(cur-dec);
                }
                inc = 0;
            }
        }
        return ans;
    }
};
