typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();
        vector<ll> curr(n);
        for(int i=0;i<n;i++) {
            curr[i]=target[i]-nums[i];
        }
        if(n==1) {
            return abs(curr[0]);
        }
        ll ans=abs(curr[0]),prev=(curr[0]>=0?1:0);
        for(int i=1;i<n;i++) {
            ll state=(curr[i]>=0?1:0);
            if(prev!=state) {
                ans+=abs(curr[i]);
            } else {
                if(prev==1)
                    ans += max(curr[i] - curr[i - 1], 0ll);
                else 
                    ans += max(curr[i - 1] - curr[i], 0ll);
            }
           prev=state;
        }
        if(curr[0]<=curr[1]) {
            // nothing to do
        }
        return ans;
        // 1 3 2
        // 2 1 4
        // 1 -2 2 1 1 1 1 1 1 
        // 1 -2 -3 -4 -5 5 6 7 8 9 1 1 1 1 1 1 1 1
        // 3 5 1 2 
        // 4 6 2 4
        // 1 1 1 2
        // 1 1 3 4
        // 4 1 3 2
        // 3 0 0 2
    }
};
