class Solution {
public:
    int sign(int num){
        if(num>0) return 1;
        if(num<0) return -1;
        return 0;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> a(n,0);

        for(int i=0;i<n;i++){
            a[i]=nums[i]-target[i];
        }

        vector<vector<int>> v;
        int prev=-2;
        for(int i=0;i<n;i++){
            int curr=sign(a[i]);

            if(curr==prev){
                v.back().push_back(abs(a[i]));
            }else{
                v.push_back({abs(a[i])});
            }

            prev=curr;
        }
        bool debug_is=false;
        long long ctr=0;
        for(auto & row:v){
            if(debug_is){
                for(auto num:row) cout<<num<<" ";cout<<endl;
            }
            int sz=row.size();
            long long curr_ctr=row.front();
            for(int i=1;i<sz;i++){
                if(row[i]>row[i-1]){
                    curr_ctr+=row[i]-row[i-1];
                }
            }
            if(debug_is) cout<<"curr_ctr : "<<curr_ctr<<endl;
            ctr+=curr_ctr;
        }

        if(debug_is) cout<<"final_ctr : "<<ctr<<endl;

        return ctr;

    }
};
