class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
int n=nums.size();
     int s=0;
     int mx=0;
     int mn=0;
     while(s<n){

      int d=nums[s]-target[s];


    if(d>0&&mx>0)
      ans+=abs(d)-min(abs(d),mx);



     else if(d<0&&mx<0){
        ans+=abs(d)-min(abs(d),abs(mx));
     }
    else {
        ans+=abs(d);
    }
    mx=d;
    s++;
     }
return ans;
    }
};
