class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
vector<int> res(n,0);
for(int i=0;i<n;i++)
{
    res[i]=b[i]-a[i];
}
long long ans=0;
ans=abs(res[0]);
for(int i=1;i<n;i++)
{
    if((res[i]>0 && res[i-1]>0)||(res[i-1]<0 && res[i]<0))
    {
        int x=abs(res[i]);
        int y=abs(res[i-1]);
        if(x<=y)
        continue;
        else
        ans+=abs(y-x);
    }else{
        ans+=abs(res[i]);
    }
}
return ans;
    }
};
